<template>
  <div class="car-selection">
    <div v-if="loading" class="loading-state">
      <div class="spinner"></div>
      <p>Loading available cars...</p>
    </div>

    <div v-else-if="error" class="error-state">
      <div class="error-icon">‚ö†Ô∏è</div>
      <h3>Failed to Load Cars</h3>
      <p>{{ error }}</p>
      <button @click="loadCars" class="retry-button">
        Try Again
      </button>
    </div>

    <div v-else class="car-grid">
      <div
        v-for="car in availableCars"
        :key="car.id"
        class="car-card"
        :class="{ 
          'selected': selectedCar?.id === car.id,
          'unavailable': !isCarAvailable(car.id)
        }"
        @click="handleCarSelection(car)"
      >
        <div class="car-header">
          <h3 class="car-name">{{ car.name }}</h3>
          <div class="car-manufacturer">{{ car.manufacturer }} {{ car.year }}</div>
        </div>

        <div class="car-specs">
          <div class="spec-row">
            <span class="spec-label">Power:</span>
            <span class="spec-value">{{ car.specifications.horsepower }} HP</span>
          </div>
          <div class="spec-row">
            <span class="spec-label">Weight:</span>
            <span class="spec-value">{{ car.specifications.weight }} kg</span>
          </div>
          <div class="spec-row">
            <span class="spec-label">Drivetrain:</span>
            <span class="spec-value">{{ car.specifications.drivetrain }}</span>
          </div>
          <div class="spec-row">
            <span class="spec-label">0-60 mph:</span>
            <span class="spec-value">{{ car.specifications.zeroToSixty }}s</span>
          </div>
          <div class="spec-row">
            <span class="spec-label">Top Speed:</span>
            <span class="spec-value">{{ car.specifications.topSpeed }} km/h</span>
          </div>
        </div>

        <div class="car-actions">
          <button
            v-if="selectedCar?.id === car.id"
            class="action-button selected-button"
            disabled
          >
            ‚úì Selected
          </button>
          <button
            v-else-if="isCarAvailable(car.id)"
            class="action-button select-button"
            @click.stop="handleCarSelection(car)"
          >
            Select Car
          </button>
          <button
            v-else
            class="action-button unavailable-button"
            disabled
          >
            Unavailable
          </button>
        </div>

        <button
          class="details-button"
          @click.stop="toggleDetails(car.id)"
        >
          {{ showDetails[car.id] ? 'Hide Details' : 'Show Details' }}
        </button>

        <div v-if="showDetails[car.id]" class="car-details">
          <h4>Detailed Specifications</h4>
          <div class="details-grid">
            <div class="detail-item">
              <span class="detail-label">Drag Coefficient:</span>
              <span class="detail-value">{{ car.specifications.dragCoefficient }}</span>
            </div>
            <div class="detail-item">
              <span class="detail-label">Frontal Area:</span>
              <span class="detail-value">{{ car.specifications.frontalArea }} m¬≤</span>
            </div>
            <div class="detail-item">
              <span class="detail-label">Tire Grip:</span>
              <span class="detail-value">{{ car.specifications.tireGrip }}</span>
            </div>
            <div class="detail-item">
              <span class="detail-label">Downforce:</span>
              <span class="detail-value">{{ car.specifications.aeroDownforce }} kg @ 100mph</span>
            </div>
            <div class="detail-item">
              <span class="detail-label">Fuel Economy:</span>
              <span class="detail-value">{{ car.specifications.fuelEconomy }} L/100km</span>
            </div>
            <div class="detail-item">
              <span class="detail-label">Gear Ratios:</span>
              <span class="detail-value">{{ car.specifications.gearRatios.join(', ') }}</span>
            </div>
          </div>
          
          <div class="licensing-info">
            <h5>Licensing Information</h5>
            <p><strong>Source:</strong> {{ car.licensing.source }}</p>
            <p><strong>Valid Until:</strong> {{ formatDate(car.licensing.validUntil) }}</p>
            <div v-if="car.licensing.restrictions.length > 0">
              <strong>Restrictions:</strong>
              <ul>
                <li v-for="restriction in car.licensing.restrictions" :key="restriction">
                  {{ restriction }}
                </li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    </div>

    <div v-if="!loading && availableCars.length === 0" class="empty-state">
      <div class="empty-icon">üèéÔ∏è</div>
      <h3>No Cars Available</h3>
      <p>There are currently no cars available for selection.</p>
    </div>
  </div>
</template>

<script setup lang="ts">
import { ref, onMounted, computed } from 'vue';
import type { CarModel } from '@shared/types/index';
import { CarService } from '../services/CarService';
import { useGameStore } from '../stores/game';
import { useErrorStore } from '../stores/error';

// Props
interface Props {
  maxSelections?: number;
}

const props = withDefaults(defineProps<Props>(), {
  maxSelections: 1
});

// Emits
const emit = defineEmits<{
  carSelected: [car: CarModel];
  selectionCleared: [];
}>();

// Stores
const gameStore = useGameStore();
const errorStore = useErrorStore();

// State
const loading = ref(false);
const error = ref<string | null>(null);
const showDetails = ref<Record<string, boolean>>({});

// Computed
const availableCars = computed(() => gameStore.availableCars);
const selectedCar = computed(() => gameStore.selectedCar);

// Methods
const loadCars = async () => {
  loading.value = true;
  error.value = null;
  
  try {
    const cars = await CarService.getAvailableCars();
    gameStore.setAvailableCars(cars);
    
    // Check availability for each car
    for (const car of cars) {
      try {
        const available = await CarService.isCarAvailable(car.id);
        carAvailability.value[car.id] = available;
      } catch {
        carAvailability.value[car.id] = true; // Default to available if check fails
      }
    }
  } catch (err) {
    const errorMessage = err instanceof Error ? err.message : 'Failed to load cars';
    error.value = errorMessage;
    errorStore.addError('Failed to load available cars', errorMessage);
  } finally {
    loading.value = false;
  }
};

const carAvailability = ref<Record<string, boolean>>({});

const isCarAvailable = (carId: string): boolean => {
  return carAvailability.value[carId] ?? true;
};

const handleCarSelection = async (car: CarModel) => {
  if (!isCarAvailable(car.id)) {
    errorStore.addError('Car Unavailable', 'This car is currently not available for selection');
    return;
  }

  try {
    // Validate car selection
    const isValid = await validateCarSelection(car);
    if (!isValid) {
      return;
    }

    gameStore.selectCar(car);
    emit('carSelected', car);
    
    errorStore.addSuccess('Car Selected', `${car.manufacturer} ${car.name} has been selected`);
  } catch (err) {
    const errorMessage = err instanceof Error ? err.message : 'Failed to select car';
    errorStore.addError('Selection Failed', errorMessage);
  }
};

const validateCarSelection = async (car: CarModel): Promise<boolean> => {
  // Check if car is still available on the server
  try {
    const isAvailable = await CarService.isCarAvailable(car.id);
    if (!isAvailable) {
      errorStore.addError('Car Unavailable', 'This car is no longer available');
      return false;
    }
    return true;
  } catch (err) {
    errorStore.addError('Validation Failed', 'Could not validate car availability');
    return false;
  }
};

const toggleDetails = (carId: string) => {
  showDetails.value[carId] = !showDetails.value[carId];
};

const formatDate = (date: Date): string => {
  return new Date(date).toLocaleDateString();
};

// Lifecycle
onMounted(() => {
  loadCars();
});
</script>

