<template>
  <div class="race-interface">
    <!-- Command Input Section -->
    <div class="command-section">
      <div class="command-input-container">
        <input
          v-model="commandInput"
          @keyup.enter="sendCommand"
          @input="validateCommand"
          type="text"
          placeholder="Enter racing command (accelerate, brake, shift, pit)..."
          class="command-input"
          :disabled="!isConnected || !isInRace"
          data-testid="command-input"
        />
        <button
          @click="sendCommand"
          :disabled="!isConnected || !isInRace || !isValidCommand"
          class="send-button"
          data-testid="send-command-button"
        >
          Send
        </button>
      </div>
      <div v-if="commandError" class="command-error" data-testid="command-error">
        {{ commandError }}
      </div>
      <div class="command-help">
        <strong>Commands:</strong> accelerate [0-100], brake [0-100], shift [gear], pit [tires/fuel]
      </div>
    </div>

    <!-- Race State Display -->
    <div class="race-state-section">
      <div class="race-header">
        <h3>üèÅ Race Status</h3>
        <div v-if="currentRace" class="race-info">
          Lap {{ currentRace.currentLap }}/{{ currentRace.totalLaps }} ‚Ä¢ 
          {{ formatTime(currentRace.raceTime) }}
        </div>
      </div>

      <!-- My Car Status -->
      <div v-if="myParticipant" class="my-status" data-testid="my-status">
        <h4>Your Position: #{{ myParticipant.position }}</h4>
        <div class="status-grid">
          <div class="status-item">
            <span class="label">Speed:</span>
            <span class="value">{{ Math.round(myParticipant.speed) }} km/h</span>
          </div>
          <div class="status-item">
            <span class="label">Lap Time:</span>
            <span class="value">{{ formatTime(myParticipant.lapTime) }}</span>
          </div>
          <div class="status-item">
            <span class="label">Total Time:</span>
            <span class="value">{{ formatTime(myParticipant.totalTime) }}</span>
          </div>
          <div class="status-item">
            <span class="label">Last Command:</span>
            <span class="value">{{ myParticipant.lastCommand || 'None' }}</span>
          </div>
        </div>

        <!-- Fuel and Tire Indicators -->
        <div class="indicators">
