              <span class="indicator-value">{{ Math.round(myParticipant.fuel) }}%</span>
            </div>
            <div class="progress-bar">
              <div 
                class="progress-fill fuel-fill"
                :style="{ width: `${myParticipant.fuel}%` }"
                :class="getFuelStatusClass(myParticipant.fuel)"
              ></div>
            </div>
          </div>

