</template>

<script setup lang="ts">
import { ref, computed } from 'vue'
import { useGameStore } from '../stores/game'
import { useWebSocketStore } from '../stores/websocket'
import type { Command, CommandType, CommandParameters } from '@shared/types/commands'

const gameStore = useGameStore()
const websocketStore = useWebSocketStore()

// Component state
const commandInput = ref('')
const commandError = ref('')
const isValidCommand = ref(false)

// Computed properties
const isConnected = computed(() => websocketStore.isConnected)
const isInRace = computed(() => gameStore.isInRace)
const currentRace = computed(() => gameStore.currentRace)
const myParticipant = computed(() => gameStore.myParticipant)
const sortedParticipants = computed(() => gameStore.sortedParticipants)
const recentEvents = computed(() => gameStore.recentEvents)

// Command validation
function validateCommand() {
  commandError.value = ''
  isValidCommand.value = false

  const input = commandInput.value.trim().toLowerCase()
  if (!input) return

  const parts = input.split(' ')
  const commandType = parts[0] as CommandType

  try {
    parseCommand(input)
    isValidCommand.value = true
  } catch (error) {
    commandError.value = error instanceof Error ? error.message : 'Invalid command'
    isValidCommand.value = false
  }
}

// Command parsing
function parseCommand(input: string): Command {
  const parts = input.trim().toLowerCase().split(' ')
  const commandType = parts[0] as CommandType
  const timestamp = Date.now()

  switch (commandType) {
    case 'accelerate':
    case 'brake': {
      const intensity = parts[1] ? parseInt(parts[1]) : 50
      if (isNaN(intensity) || intensity < 0 || intensity > 100) {
        throw new Error(`${commandType} intensity must be between 0-100`)
      }
      return {
        type: commandType,
        parameters: { intensity },
        timestamp
      }
    }

    case 'shift': {
      const gear = parts[1] ? parseInt(parts[1]) : undefined
      if (gear === undefined || isNaN(gear) || gear < 1 || gear > 8) {
        throw new Error('Shift command requires gear number (1-8)')
      }
      return {
        type: commandType,
        parameters: { gear },
        timestamp
      }
    }

    case 'pit': {
      const action = parts[1]
      const parameters: CommandParameters = {
        pitAction: {
          changeTires: action === 'tires' || action === 'both',
          refuel: action === 'fuel' || action === 'both'
        }
      }
      
      if (!parameters.pitAction?.changeTires && !parameters.pitAction?.refuel) {
        throw new Error('Pit command requires action: tires, fuel, or both')
      }

      return {
        type: commandType,
        parameters,
        timestamp
      }
    }

    default:
      throw new Error('Unknown command. Use: accelerate, brake, shift, or pit')
  }
}

// Send command
function sendCommand() {
  if (!isValidCommand.value || !commandInput.value.trim()) return

  try {
    const command = parseCommand(commandInput.value)
    websocketStore.sendMessage('race:command', command)
    commandInput.value = ''
    commandError.value = ''
    isValidCommand.value = false
  } catch (error) {
    commandError.value = error instanceof Error ? error.message : 'Failed to send command'
  }
}

// Utility functions
function formatTime(seconds: number): string {
  if (!seconds || seconds < 0) return '0:00.000'
  
  const minutes = Math.floor(seconds / 60)
  const secs = Math.floor(seconds % 60)
  const milliseconds = Math.floor((seconds % 1) * 1000)
  
  return `${minutes}:${secs.toString().padStart(2, '0')}.${milliseconds.toString().padStart(3, '0')}`
}

function getFuelStatusClass(fuel: number): string {
  if (fuel <= 10) return 'critical'
  if (fuel <= 25) return 'warning'
  return 'good'
}

function getTireStatusClass(wear: number): string {
  if (wear >= 90) return 'critical'
  if (wear >= 75) return 'warning'
  return 'good'
}
</script>

